package Data;

public class MYoddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=8;
		System.out.println("enter a even number");
		for(int i=0;i<=n;i++) {
			if(i%2==0) {
				System.out.println(i);
			}
		
			System.out.println("enter a odd number");
			for( i=0;i<=n;i++) {
				if(i%2!=0) {
					System.out.println(i);
		}

	}

}
	}
}
